mapper.select:
SELECT RNO, BNO, REPLY, REPLIER, REPLYDATE, UPDATEDATE
FROM   TBL_REPLY
WHERE  RNO = #{rno}

mapper.update:
UPDATE TBL_REPLY
SET    REPLY = #{reply}
	 , REPLIER = #{replier}
     , UPDATEDATE = SYSDATE()
WHERE RNO = #{rno}

mapper.select:
SELECT RNO, BNO, REPLY, REPLIER, REPLYDATE, UPDATEDATE
FROM   TBL_REPLY
WHERE  BNO = #{bno}
ORDER BY RNO DESC
LIMIT #{cri.limit} OFFSET #{cri.offset}
